import React, { useState } from 'react';
import axios from 'axios';
import './loginn.css';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [keepLoggedIn, setKeepLoggedIn] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post(process.env.REACT_APP_API + '/login', {
                email,
                password,
                keepLoggedIn,
            });

            console.log(response.data);
            const access = response.data.access;

            switch (access) {
                case 0:
                    // Redirecionar para a página de administrador
                    navigate('/fetch-data');
                    break;
                case 1:
                    // Redirecionar para a página de cliente
                    navigate('/fetch-data');
                    break;
                case 2:
                    // Redirecionar para a página de artista
                    navigate('/fetch-data');
                    break;
                default:
                    // Redirecionar para uma página de erro, pois o tipo de usuário é desconhecido
                    break;
            }

            console.log(response);
        } catch (error) {
            console.error(error);
        }
    }

    return (

        <div class="main-login">
            <div class="form-image">
                <div class="texto">
                    <H1> <b>Bem Vindo!</b> <br/></H1>
                    <p>Realize o login para acessar sua conta <br/>ou cadastre-se gratuitamente</p>
                </div>
            </div>
            <div class="right-login">
                <div class="card-login">
                    <h1>Login</h1>
                    <div class="textfield">
                        <label for="email"></label>
                        <input type="text" name="email" placeholder="E-mail"/>
                    </div>
                    <br />
                    <div class="textfield">
                        <input type="password" name="senha" placeholder="Senha"/>
                    </div>
                    <br/>
                        <div class="checkbox-container">
                            <input type="checkbox" id="manter-conexao" name="manter-conexao"/>
                                <label for="manter-conexao">Manter Conectado</label>
                        </div>

                        <p class="forgot-password"><a href="#">Esqueceu sua senha?</a></p>
                        <button class="btn-login">Login</button>

                </div>
            </div>

        </div>
    );
}

export default Login;
