import React, { useState } from 'react';
import axios from 'axios';
import './loginn.css';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [keepLoggedIn, setKeepLoggedIn] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post(process.env.REACT_APP_API + '/login', {
                email,
                password,
                keepLoggedIn,
            });

            console.log(response.data);
            const access = response.data.access;

            switch (access) {
                case 0:
                    // Redirecionar para a página de administrador
                    navigate('/fetch-data');
                    break;
                case 1:
                    // Redirecionar para a página de cliente
                    navigate('/fetch-data');
                    break;
                case 2:
                    // Redirecionar para a página de artista
                    navigate('/fetch-data');
                    break;
                default:
                    // Redirecionar para uma página de erro, pois o tipo de usuário é desconhecido
                    break;
            }

            console.log(response);
        } catch (error) {
            console.error(error);
        }
    }

    return (

        <div class="main-login">
            <div class="form-image">
                <div class="texto">
                    <h1> <b>Bem Vindo!</b> <br /></h1>
                    <p>Realize o login para acessar sua conta <br/>ou cadastre-se gratuitamente</p>
                </div>
            </div>
            <div class="right-login">
                <div class="card-login">
                    <h1>Login</h1>
                    <form onSubmit={handleSubmit}>
                    <div class="textfield">
                        <label for="email"></label>
                        <input
                            type="text"
                            value={email}
                            name="email"
                            placeholder="E-mail"
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </div>
                    <br />
                    <div class="textfield">
                        <input
                            type="password"
                            value={password}
                            name="senha"
                            placeholder="Senha"
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <br />
                    <div class="checkbox-container">
                        <input
                            type="checkbox"
                            checked={keepLoggedIn}
                            id="manter-conexao"
                            name="manter-conexao"
                            onChange={() => setKeepLoggedIn(!keepLoggedIn)}
                        />
                        <label for="manter-conexao">Manter Conectado</label>
                        </div>

                        <p class="forgot-password"><a href="#">Esqueceu sua senha?</a></p>
                        <button class="btn-login" type="submit">Login</button>
                    </form>
                </div>
            </div>

        </div>
    );
}

export default Login;
