import React, { useState } from 'react';
import axios from 'axios';
import './loginn.css';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [keepLoggedIn, setKeepLoggedIn] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post(process.env.REACT_APP_API + '/login', {
                email,
                password,
                keepLoggedIn,
            });

            console.log(response.data);
            const access = response.data.access;

            switch (access) {
                case 0:
                    // Redirecionar para a página de administrador
                    navigate('/fetch-data');
                    break;
                case 1:
                    // Redirecionar para a página de cliente
                    navigate('/fetch-data');
                    break;
                case 2:
                    // Redirecionar para a página de artista
                    navigate('/fetch-data');
                    break;
                default:
                    // Redirecionar para uma página de erro, pois o tipo de usuário é desconhecido
                    break;
            }

            console.log(response);
        } catch (error) {
            console.error(error);
        }
    }

    return (

        <div className="container">
            <div className="form-image">
                <div className="texto">
                    <h1>Bem Vindo!</h1>
                    <br />
                    <p>Realize o Login para acessar sua conta.</p>
                </div>
            </div>
            <div className="right-login">
                <div className="card-login">
                    <h1>Login</h1>                    
                    <div className="input-box">
                        <input
                            type="text"
                            name="email"
                            placeholder="E-mail"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </div>
                    <div className="input-box">
                        <input
                            type="password"
                            name="senha"
                            placeholder="Senha"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <div className="checkbox-container">
                            <input
                                type="checkbox"
                                checked={keepLoggedIn}
                                onChange={() => setKeepLoggedIn(!keepLoggedIn)}
                            />
                        <label>
                            Manter-me conectado
                        </label>
                    </div>
                    <div className="continue-button">
                        <button class="btn-login" type="submit">Entrar</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
